<?phprequire_once "clases/AccesoDatos.php";require_once "clases/Usuario.php";$queMuestro = isset($_POST['queMuestro']) ? $_POST['queMuestro'] : NULL;switch ($queMuestro) {    case "2":        //LOGOUT		//IMPLEMENTAR...        session_start();        if (isset($_SESSION["Usuario"]))        {            session_unset();            echo "Ok";        }        else            echo "No Ok";        break;    case "3":        //MOSTRAR GRILLA		//IMPLEMENTAR...        require_once "grilla.php";        break;    case "4":        //MUESTRA FORM ALTA-MODIFICACION USUARIO		//IMPLEMENTAR...        require_once "form.php";        break;    case "5":        //SUBIR FOTO AL TMP		//IMPLEMENTAR...        require_once "clases/Archivo.php";        $resultado = new stdClass();        if (!isset($_FILES["imagenModificada"]))        {            $resultado->exito = FALSE;            $resultado->mensaje = "ERROR";            echo json_encode($resultado);        }        else        {            $resultado = Archivo::Subir();            echo json_encode($resultado);        }        break;    case "6"://ALTA USUARIO		//IMPLEMENTAR...        break;    case "7"://ELIMINAR USUARIO		//IMPLEMENTAR...        break;            case "8":        //MODIFICAR USUARIO		//IMPLEMENTAR...        require_once "clases/Archivo.php";        $resultado = new stdClass();        $resultado->exito = TRUE;        if (!isset($_POST["usuarioModificado"]))        {            $resultado->exito = FALSE;            $resultado->mensaje = "ERROR";            echo json_encode($resultado);        }        else        {            $usuarioModificado = json_decode($_POST["usuarioModificado"]);            $origen = $_POST["fotoNueva"];            $destino = "fotos/" . $usuarioModificado->foto;            if (Archivo::Mover($origen, $destino))            {                if (Usuario::Modificar($usuarioModificado))                    $resultado->mensaje = "El usuario ha sido modificado.";                else                {                    $resultado->mensaje = "Ocurrio un error al modificar al usuario.";                    $resultado->exito = FALSE;                }            }            else            {                $resultado->mensaje = "Ocurrio un error al guardar la imagen.";                $resultado->exito = FALSE;               }            echo json_encode($resultado);        }                break;	case "9"://ELEGIR THEME		//IMPLEMENTAR...		break;		case "10"://GUARDA EN COOKIE EL THEME		//IMPLEMENTAR...		break;		    default:        echo ":(";}