<?phpclass Archivo {    public static function Subir()    {		//IMPLEMENTAR...    	$resultado = new stdClass();    	$resultado->exito = TRUE;    	if (!file_exists($_FILES["imagenModificada"]["tmp_name"]))    	{    		$resultado->exito = FALSE;			$resultado->mensaje = "Error al subir la imagen. ";			return $resultado;    	}    	$tipoArchivo = pathinfo($_FILES["imagenModificada"]["name"], PATHINFO_EXTENSION);		$archivoTmp = date("Ymd_His") . "." . $tipoArchivo;		$destino = "tmp/" . $archivoTmp;		if(getimagesize($_FILES["imagenModificada"]["tmp_name"]) === false)		{			$resultado->exito = FALSE;			$resultado->mensaje = "El archivo seleccionado no es una imagen. ";			return $resultado;		}		if($tipoArchivo != "jpg" && $tipoArchivo != "jpeg" && $tipoArchivo != "gif" && $tipoArchivo != "png")		{				$resultado->exito = FALSE;				$resultado->mensaje = "Tipo de imagen no permitido. ";				return $resultado;		}		if (file_exists($destino))		{			$$resultado->exito = FALSE;			$resultado->mensaje = "La imagen ya se ha subido. ";			return $resultado;		}		if ($_FILES["imagenModificada"]["size"] > 500000)		{			$resultado->exito = FALSE;			$resultado->mensaje = "La imagen supera el peso maximo. ";			return $resultado;		}		if (!Archivo::Mover($_FILES["imagenModificada"]["tmp_name"], $destino))		{			$resultado->exito = FALSE;			$resultado->mensaje = "La imagen no se pudo guardar. ";			return $resultado;		}		if (substr_count($_POST["fotoAnterior"], "tmp/") > 0)			Archivo::Borrar($_POST["fotoAnterior"]);				$resultado->imagenSubida = $archivoTmp;		$resultado->imagenSubidaRuta = $destino;		return $resultado;    }    public static function Borrar($path)    {		//IMPLEMENTAR...		return unlink($path);    }    public static function Mover($pathOrigen, $pathDestino)    {		//IMPLEMENTAR...    	return move_uploaded_file($pathOrigen, $pathDestino);    }}